import { Component, Input, OnChanges, SimpleChanges } from '@angular/core';
// Required for Angular directives like ngIf
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-projectcard',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './projectcard.component.html',
  styleUrl: './projectcard.component.css'
})
export class ProjectcardComponent {
    // Receive data from the Page via @Input() property
    @Input() repoData: any;
    @Input() githubColors: any;

    // Empty Strings while waiting for data
    repoCreatedYear: string = '';
    repoUpdatedYear: string = ''

    constructor() {}

    // Lifecycle hook called when any data-bound property of a directive changes. 
    // We use this hook to update the repoCreatedYear and repoUpdatedYear properties whenever repoData changes.
    ngOnChanges(changes: SimpleChanges): void {
        if (changes['repoData'] && this.repoData) {
            // Extract the years when we receive the data
            this.repoCreatedYear = this.extractDateYear(this.repoData.created_at);
            this.repoUpdatedYear = this.extractDateYear(this.repoData.updated_at);
        }
    }

    // Helper function to extract date
    extractDateYear(date: Date) {
        // Extract the Datestamp from the RepoData and extract only the year
        const createdDate = new Date(date);
        return createdDate.getFullYear().toString();
    }
}
<!-- Show only if repoData is not NULL -->
<div *ngIf="repoData" class="project-card">
    <div class="project-card-content">
        <!-- Includes Title and Project Content -->
        <h3>{{ repoCreatedYear }}</h3>
        <h1>{{ repoData.name }}</h1>
        <p>{{ repoData.description }}</p>
        <p>Updated On: {{ repoUpdatedYear }}</p>

        <div class="project-card-details">
            <!-- Includes the language, number of stars, forks, source and demo -->
            <div class="project-card-stats">
                <!-- Language -->
                <div *ngIf="repoData.language" class="language-container">
                    <span *ngIf="githubColors[repoData.language]" 
                        [ngStyle]="{ 'background-color': githubColors[repoData.language]?.color }" 
                        class="language-dot"></span>
                    <span>{{ repoData.language }}</span>
                </div>

                <!-- Stars -->
                <div *ngIf="repoData.stargazers_count" class="stars-container">
                    <svg class="icon star-icon" aria-label="stars" viewBox="0 0 16 16" version="1.1" width="16" height="16" role="img">
                        <path fill-rule="evenodd" d="M8 .25a.75.75 0 01.673.418l1.882 3.815 4.21.612a.75.75 0 01.416 1.279l-3.046 2.97.719 4.192a.75.75 0 01-1.088.791L8 12.347l-3.766 1.98a.75.75 0 01-1.088-.79l.72-4.194L.818 6.374a.75.75 0 01.416-1.28l4.21-.611L7.327.668A.75.75 0 018 .25zm0 2.445L6.615 5.5a.75.75 0 01-.564.41l-3.097.45 2.24 2.184a.75.75 0 01.216.664l-.528 3.084 2.769-1.456a.75.75 0 01.698 0l2.77 1.456-.53-3.084a.75.75 0 01.216-.664l2.24-2.183-3.096-.45a.75.75 0 01-.564-.41L8 2.694v.001z"></path>
                    </svg>
                    &nbsp; <span>{{ repoData.stargazers_count }}</span>
                </div>

                <!-- Forks -->
                <div *ngIf="repoData.forks" class="forks-container">
                    <svg class="icon fork-icon" aria-label="fork" viewBox="0 0 16 16" version="1.1" width="16" height="16" role="img">
                        <path fill-rule="evenodd" d="M5 3.25a.75.75 0 11-1.5 0 .75.75 0 011.5 0zm0 2.122a2.25 2.25 0 10-1.5 0v.878A2.25 2.25 0 005.75 8.5h1.5v2.128a2.251 2.251 0 101.5 0V8.5h1.5a2.25 2.25 0 002.25-2.25v-.878a2.25 2.25 0 10-1.5 0v.878a.75.75 0 01-.75.75h-4.5A.75.75 0 015 6.25v-.878zm3.75 7.378a.75.75 0 11-1.5 0 .75.75 0 011.5 0zm3-8.75a.75.75 0 100-1.5.75.75 0 000 1.5z"></path>
                    </svg>
                    &nbsp; <span>{{ repoData.forks }}</span>
                </div>
            </div>

            <div class="project-card-actions">
                <!-- Open the link in a new tab -->
                <a [href]="repoData.html_url" target="_blank" type="button" class="link-button">Source</a>
                <button class="link-button">Demo</button>
            </div>
        </div>
    </div>
</div>
